import React, { Component } from "react";
import NewsItem from "./NewsItem";
import Spinner from "./Spinner";
import PropTypes from "prop-types";
import InfiniteScroll from "react-infinite-scroll-component";

export class News extends Component {
  static defaultProps = {
    country: "in",
    pageSize: 8,
    category: "general",
  };

  static propTypes = {
    country: PropTypes.string,
    pageSize: PropTypes.number,
    category: PropTypes.string,
    apiKey: PropTypes.string.isRequired, // Expecting the API key to be passed as prop
    setProgress: PropTypes.func.isRequired, // Assuming setProgress function is passed as prop
  };

  capitalizeFirstLetter = (string) => {
    return string.charAt(0).toUpperCase() + string.slice(1);
  };

  constructor(props) {
    super(props);
    this.state = {
      articles: [],
      loading: true,
      page: 1,
      totalResults: 0,
    };
    document.title = `${this.capitalizeFirstLetter(
      this.props.category
    )} - NewsMonkey`;
  }

  // Fetch and update news articles
  async updateNews() {
    this.props.setProgress(10);
    const { country, category, apiKey, pageSize } = this.props;
    const { page } = this.state;
    const url = `https://newsapi.org/v2/top-headlines?country=${country}&category=${category}&apiKey=${apiKey}&page=${page}&pageSize=${pageSize}`;

    try {
      this.setState({ loading: true });
      let data = await fetch(url);
      this.props.setProgress(30);
      let parsedData = await data.json();
      this.props.setProgress(70);
      this.setState({
        articles: parsedData.articles || [],
        totalResults: parsedData.totalResults,
        loading: false,
      });
      this.props.setProgress(100);
    } catch (error) {
      console.error("Error fetching news:", error);
      this.setState({ loading: false });
      this.props.setProgress(100);
    }
  }

  async componentDidMount() {
    this.updateNews();
  }

  // Handlers for page navigation
  handlePrevClick = async () => {
    if (this.state.page === 1) return; // Prevent navigating to a negative page
    this.setState({ page: this.state.page - 1 }, () => {
      this.updateNews(); // Ensure the update happens after state is set
    });
  };

  handleNextClick = async () => {
    if (this.state.page * this.props.pageSize >= this.state.totalResults)
      return; // Prevent navigating past the last page
    this.setState({ page: this.state.page + 1 }, () => {
      this.updateNews(); // Ensure the update happens after state is set
    });
  };

  // Fetch more data for infinite scroll
  fetchMoreData = async () => {
    if (this.state.page * this.props.pageSize >= this.state.totalResults)
      return; // Prevent fetching more if all data is loaded
    this.setState({ page: this.state.page + 1 }, async () => {
      const { country, category, apiKey, pageSize } = this.props;
      const { page } = this.state;
      const url = `https://newsapi.org/v2/top-headlines?country=${country}&category=${category}&apiKey=${apiKey}&page=${page}&pageSize=${pageSize}`;

      try {
        let data = await fetch(url);
        let parsedData = await data.json();
        this.setState({
          articles: this.state.articles.concat(parsedData.articles || []), // Safely concatenate new articles
          totalResults: parsedData.totalResults,
        });
      } catch (error) {
        console.error("Error fetching more data:", error);
      }
    });
  };

  render() {
    return (
      <>
        <h1 className="text-center" style={{ margin: "35px 0px" }}>
          NewsMonkey - Top {this.capitalizeFirstLetter(this.props.category)}{" "}
          Headlines
        </h1>
        {this.state.loading && <Spinner />}
        <InfiniteScroll
          dataLength={this.state.articles.length}
          next={this.fetchMoreData}
          hasMore={this.state.articles.length < this.state.totalResults}
          loader={<Spinner />}
        >
          <div className="container">
            <div className="row">
              {this.state.articles.length > 0 ? (
                this.state.articles.map((element) => (
                  <div className="col-md-4" key={element.url}>
                    <NewsItem
                      title={element.title || "No title available"}
                      description={
                        element.description || "No description available"
                      }
                      imageUrl={element.urlToImage}
                      newsUrl={element.url}
                      author={element.author}
                      date={element.publishedAt}
                      source={element.source.name}
                    />
                  </div>
                ))
              ) : (
                <div className="col-12">
                  No articles available at the moment
                </div>
              )}
            </div>
          </div>
        </InfiniteScroll>
      </>
    );
  }
}

export default News;
